代码



关于C++传引用和传参数的理解代码#include &lt;iostream&gt;using namespace std; void test(int &amp;a)&#123;    a = 3;    cout &lt;&lt; &amp;a &lt;&lt; &quot; &quot; &lt;&lt; a &lt;&lt; endl;&#125; int main(void)&#123;    int a = 1;    cout &lt;&lt; &amp;a &lt;&lt; &quot; &quot; &lt;&lt; a &lt;&lt; endl;    test(a);    cout &lt;&lt; &amp;a &lt;&lt; &quot; &quot; &lt;&lt; a &lt;&lt; endl;    return 0;&#125;

学习了一些基础算法
首先是前缀和的算法#include &lt;iostream&gt;using namespace std;const int N = 1e6 + 10;int a[N],S[N];int n,m,l,r;int main()&#123;    cin &gt;&gt; n&gt;&gt; m;    for(int i=1;i&lt;=n;i++)    &#123;cin &gt;&gt; a[i];&#125;    S[0] = 0;    for(int i=1;i&lt;=n;i++)    &#123;S[i]=S[i-1]+a[i];&#125;    while(m--)    &#123;        cin &gt;&gt; l &gt;&gt;r;        cout &lt;&lt; (S[r]-S[l-1]) &lt;&lt;endl;    &#125;    return 0;&#125;
接下来是差分算法，实际上是对于前缀和的逆运算#include &lt;iostream&gt;using namespace std;const int N = 1e6 + 10;int a[N],b[N];int n,m,l,r,c;void insert(int l,int r,int c)&#123;    b[l] +=c;    b[r+1] -= c;&#125;int main()&#123;    cin &gt;&gt; n &gt;&gt;m;    for(int i=1;i&lt;=n;i++) cin &gt;&gt; a[i];    for(int i=1;i&lt;=n;i++) insert(i,i,a[i]);    while(m--)    &#123;        cin &gt;&gt; l&gt;&gt;r&gt;&gt;c;        insert(l,r,c);    &#125;    for(int i=1;i&lt;=n;i++) b[i]+=b[i-1];    for(int i=1;i&lt;=n;i++) cout &lt;&lt;b[i]&lt;&lt;&quot; &quot;;    return 0;&#125;
实际上他们也都有各自的二维算法这是前缀和#include &lt;iostream&gt;using namespace std;const int N = 1010;int a[N][N],s[N][N];int main()&#123;    int n,m,q;    cin &gt;&gt; n&gt;&gt; m &gt;&gt;q;    for(int i=1;i&lt;=n;i++)        for(int j =1;j&lt;=m;j++)        &#123;  cin &gt;&gt; a[i][j];            s[i][j] = s[i][j-1] + s[i-1][j] -s[i-1][j-1] +a[i][j];        &#125;    while(q--)    &#123;        int x1,y1,x2,y2;        cin &gt;&gt; x1 &gt;&gt; y1&gt;&gt; x2 &gt;&gt; y2;        cout &lt;&lt; (s[x2][y2]-s[x2][y1-1]-s[x1-1][y2]+s[x1-1][y1-1]) &lt;&lt;endl;    &#125;    return 0;&#125;
这是差分矩阵#include &lt;iostream&gt;using namespace std;const int N = 1010;int a[N][N],b[N][N];int n,m,q,x1,y1,x2,y2,c;void insert(int x1,int y1,int x2,int y2,int c)&#123;    b[x1][y1] +=c;    b[x2+1][y1] -= c;    b[x1][y2+1] -= c;    b[x2+1][y2+1] += c;&#125;int main()&#123;    cin &gt;&gt; n &gt;&gt;m &gt;&gt;q;    for(int i=1;i&lt;=n;i++)         for(int j=1;j&lt;=m;j++)            cin &gt;&gt; a[i][j];    for(int i=1;i&lt;=n;i++)         for(int j=1;j&lt;=m;j++)            insert(i,j,i,j,a[i][j]);    while(q--)    &#123;        cin &gt;&gt; x1&gt;&gt;y1&gt;&gt;x2&gt;&gt;y2&gt;&gt;c;        insert(x1,y1,x2,y2,c);    &#125;    for(int i=1;i&lt;=n;i++)        for(int j=1;j&lt;=m;j++)            b[i][j] += b[i-1][j] + b[i][j-1] -b[i-1][j-1];        for(int i=1;i&lt;=n;i++)        &#123;            for(int j=1;j&lt;=m;j++)            &#123;                cout &lt;&lt; b[i][j] &lt;&lt;&quot; &quot;;            &#125;            cout &lt;&lt;endl;        &#125;    return 0;&#125;
